import React, { useEffect, useState } from "react";

const API_URL = "http://localhost:5001/api/tasks";

function App() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: "", description: "" });
  const [editing, setEditing] = useState(null);
  const [error, setError] = useState("");

  const fetchTasks = async () => {
    try {
      setError("");
      const res = await fetch(API_URL);
      if (!res.ok) throw new Error("Failed to fetch tasks");
      setTasks(await res.json());
    } catch (err) {
      setTasks([]);
      setError("Cannot connect to backend. Please make sure the server is running.");
    }
  };

  useEffect(() => { fetchTasks(); }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      setError("");
      if (editing) {
        await fetch(`${API_URL}/${editing._id}` , {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ ...form, completed: editing.completed }),
        });
        setEditing(null);
      } else {
        await fetch(API_URL, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(form),
        });
      }
      setForm({ title: "", description: "" });
      fetchTasks();
    } catch (err) {
      setError("Cannot connect to backend. Please make sure the server is running.");
    }
  };

  const handleEdit = task => {
    setForm({ title: task.title, description: task.description });
    setEditing(task);
  };

  const handleDelete = async id => {
    try {
      setError("");
      await fetch(`${API_URL}/${id}`, { method: "DELETE" });
      fetchTasks();
    } catch (err) {
      setError("Cannot connect to backend. Please make sure the server is running.");
    }
  };

  const toggleComplete = async task => {
    try {
      setError("");
      await fetch(`${API_URL}/${task._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...task, completed: !task.completed }),
      });
      fetchTasks();
    } catch (err) {
      setError("Cannot connect to backend. Please make sure the server is running.");
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f8fafc 0%, #e0e7ff 100%)', padding: 0, margin: 0 }}>
      <div style={{ maxWidth: 500, margin: "40px auto", background: "#fff", borderRadius: 16, boxShadow: "0 4px 24px rgba(0,0,0,0.08)", padding: 32 }}>
        <h1 style={{ textAlign: "center", color: "#4f46e5", marginBottom: 24, fontWeight: 700, letterSpacing: 1 }}>📝 Task Manager</h1>
        {error && (
          <div style={{ background: '#fee2e2', color: '#b91c1c', padding: '12px 18px', borderRadius: 8, marginBottom: 18, textAlign: 'center', fontWeight: 500 }}>
            {error}
          </div>
        )}
        <form onSubmit={handleSubmit} style={{ display: "flex", gap: 12, marginBottom: 24, flexWrap: 'wrap', justifyContent: 'center' }}>
          <input
            name="title"
            placeholder="Title"
            value={form.title}
            onChange={handleChange}
            required
            style={{ flex: 1, minWidth: 120, padding: 10, borderRadius: 8, border: "1px solid #c7d2fe", fontSize: 16 }}
          />
          <input
            name="description"
            placeholder="Description"
            value={form.description}
            onChange={handleChange}
            style={{ flex: 2, minWidth: 120, padding: 10, borderRadius: 8, border: "1px solid #c7d2fe", fontSize: 16 }}
          />
          <button
            type="submit"
            style={{ background: "#6366f1", color: "#fff", border: "none", borderRadius: 8, padding: "10px 18px", fontWeight: 600, cursor: "pointer", fontSize: 16 }}
          >
            {editing ? "Update" : "Add"}
          </button>
          {editing && (
            <button
              type="button"
              onClick={() => { setEditing(null); setForm({ title: "", description: "" }); }}
              style={{ background: "#f3f4f6", color: "#4b5563", border: "none", borderRadius: 8, padding: "10px 18px", fontWeight: 600, cursor: "pointer", fontSize: 16 }}
            >
              Cancel
            </button>
          )}
        </form>
        <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
          {tasks.length === 0 && !error && <li style={{ textAlign: 'center', color: '#9ca3af' }}>No tasks yet.</li>}
          {tasks.map(task => (
            <li key={task._id} style={{
              margin: "12px 0", display: "flex", alignItems: "center", justifyContent: "space-between", background: task.completed ? "#e0e7ff" : "#f3f4f6", borderRadius: 10, padding: "12px 18px", boxShadow: task.completed ? "0 2px 8px #6366f133" : "none"
            }}>
              <div style={{ flex: 1, cursor: "pointer" }} onClick={() => toggleComplete(task)}>
                <div style={{
                  textDecoration: task.completed ? "line-through underline" : "underline",
                  color: task.completed ? "#6366f1" : "#111827",
                  fontWeight: 600,
                  fontSize: 18,
                  marginBottom: 4
                }}>
                  {task.title}
                </div>
                <div style={{ color: "#6b7280", fontSize: 15, marginLeft: 2, marginBottom: 2 }}>{task.description}</div>
                <div style={{ display: 'inline-block', marginTop: 4 }}>
                  <span style={{
                    display: 'inline-block',
                    padding: '2px 12px',
                    borderRadius: 8,
                    background: task.completed ? '#22c55e' : '#d1d5db',
                    color: task.completed ? '#fff' : '#374151',
                    fontWeight: 600,
                    fontSize: 13,
                    letterSpacing: 1,
                  }}>
                    {task.completed ? 'Done' : 'Todo'}
                  </span>
                </div>
              </div>
              <div style={{ display: "flex", gap: 8 }}>
                <button
                  onClick={() => handleEdit(task)}
                  style={{ background: "#fbbf24", color: "#fff", border: "none", borderRadius: 6, padding: "6px 12px", fontWeight: 500, cursor: "pointer", fontSize: 15 }}
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(task._id)}
                  style={{ background: "#ef4444", color: "#fff", border: "none", borderRadius: 6, padding: "6px 12px", fontWeight: 500, cursor: "pointer", fontSize: 15 }}
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div style={{ textAlign: 'center', color: '#a5b4fc', marginTop: 24, fontSize: 15 }}>
        Made with <span style={{ color: '#6366f1' }}>React</span> & <span style={{ color: '#6366f1' }}>Express</span>
      </div>
    </div>
  );
}

export default App;
